import styled from "styled-components";

const Buttons = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  font-size: 1rem;
  ${(props) =>
    props.primary &&
    css`
      color: white;
    `}
`;

export default Buttons;
