import styled from 'styled-components';
import React, { useState } from 'react';
import Issue from './issue';

const Container = styled.div``;

const Section = styled.div``;

const Tablist = styled.div``;

const TabUlist = styled.ul`
  display: flex;
  width: 100%;
  padding: 0px 32px;
`;

const IssueTab = styled.li`
    font-size: 1.2rem;
    width: 100%;
    text-align: center;
    border-radius: ${(props) => (props.isActive ? '' : '6px 6px 0px 0px')};
    border-bottom: ${(props) =>
      props.isActive ? '' : '1px solid rgb(225, 228, 232)'};
    border-top: ${(props) =>
      props.isActive ? '1px solid rgb(225, 228, 232)' : ''};
    border-right: ${(props) =>
      props.isActive ? '1px solid rgb(225, 228, 232)' : ''};
    border-left: ${(props) =>
      props.isActive ? '1px solid rgb(225, 228, 232)' : ''};

    span {
      cursor: pointer;
      color: rgb(88, 96, 105);
      padding: 16px;
      display: block;
      width: 100%;
    }
}
`;

const PullreqTab = styled.li`
  font-size: 1.2rem;
  width: 100%;
  text-align: center;
  border-radius: ${(props) => (props.isActive ? '6px 6px 0px 0px' : '')};
  border-bottom: ${(props) =>
    props.isActive ? '1px solid rgb(225, 228, 232)' : ''};
  border-top: ${(props) =>
    props.isActive ? '' : '1px solid rgb(225, 228, 232)'};
  border-right: ${(props) =>
    props.isActive ? '' : '1px solid rgb(225, 228, 232)'};
  border-left: ${(props) =>
    props.isActive ? '' : '1px solid rgb(225, 228, 232)'};

  span {
    cursor: pointer;
    color: rgb(88, 96, 105);
    padding: 16px;
    display: block;
    width: 100%;
  }
`;

const IssueTags = styled.span`
  cursor: pointer;
  color: rgb(88, 96, 105);
  padding: 16px;
  display: block;
  width: 100%;
`;

const PullreqTags = styled.span`
  cursor: pointer;
  color: rgb(88, 96, 105);
  padding: 16px;
  display: block;
  width: 100%;
`;

function Home() {
  const [state, setState] = useState(true);
  return (
    <Container>
      <Section>
        <Tablist>
          <TabUlist>
            <IssueTab onClick={() => setState(true)} isActive={state}>
              <IssueTags>Issue</IssueTags>
            </IssueTab>
            <PullreqTab onClick={() => setState(false)} isActive={state}>
              <PullreqTags>Pull Request</PullreqTags>
            </PullreqTab>
          </TabUlist>
        </Tablist>
        if({state}){<Issue />}
      </Section>
    </Container>
  );
}

export default Home;
