  var MM = date.getMonth()+1;
  var DD = date.getDate();

  if(MM <10){
    MM = "0" + MM;
  }
  if(DD < 10){
    DD = "0" + DD;
  }

  return MM + "-" + DD + "-" + YYYY;
}

const userData = {
  name: 'jiro',
  mail: 'hoge@example.com',
}

const initialData = {
