      ctuser: '',
      ctdate: getdate(),
      update: getdate(),
    },
    3:{
      id: 3,
      title: 'fix layout',
      status: 'Open',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
      ctuser: '',
      ctdate: getdate(),
      update: getdate(),
    },
};

const initialState = {
  index: Object.values(initialData).length,
  data: initialData
}

const reducer = (state = initialState, action) => {
  let newState = {...state}
  switch (action.type) {
    case 'add_issue':
      const { title, description } = action.payload || {}
      newState.index++
      newState.data[newState.index] = { id: newState.index, title: title, status: 'Open', ctuser: userData.name, ctdate: getdate(), update: getdate()};
      return newState;
    case 'filter_issue':
      const greptxt = action.payload
      if(!greptxt) return initialState;
      newState.data = Object.values(initialData).filter( function( value ) {
        return value.title.includes(greptxt)
      })
    case 'delete_issue':

    return newState
    default:
    return state;
  }
};

const store = createStore(reducer);
export default store;
