import styled from 'styled-components';
import { connect } from 'react-redux';
import store from '../store';

const Searchblock = styled.div`
  padding: 8px 16px;
  display: flex;
  width: 100%;
  .text-field-container {
    width: 100%;
  }
`;

const Searchtext = styled.div`
  border-radius: 6px;
  border: 1px solid rgb(225, 228, 232);
`;

const Searchinput = styled.input`
  padding: 8px;
  border: none;
  outline: none;
  background: none;
  font-size: 1rem;
  width: 100%;
`;

const Search = (filter_issue) => {

  const onChangeSearch = (e) => {

    filter_issue(e.target.value)
  }

  return (
    <Searchblock store={store}>
      <Searchtext className="text-field-container">
        <Searchinput onChange={onChangeSearch} type="input" placeholder="issue名で検索" />
      </Searchtext>
    </Searchblock>
  );
};

const mapStateToProps = (state) => {
  return { issue: state.data };
};

const mapDispatchToProps = (dispatch) => {
  return {
    filter_issue: (greptxt) => dispatch({type: 'filter_issue', payload: greptxt}),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Search);
