import { Routes, Route, Link } from 'react-router-dom';
import styled from 'styled-components';

const Header = styled.div`
  display: flex;
  padding: 16px;
  -webkit-box-pack: justify;
  justify-content: space-between;
  -webkit-box-align: center;
  align-items: center;
  width: 100%;
  background: rgb(51, 51, 51);
  color: white;
`;

const Gihtubviewer = styled.h1`
  white-space: nowrap;
  color: white;
`;

const Ulist = styled.ul`
  display: flex;
  width: 100%;
  padding: 0px 32px;

  li {
    margin-right: 16px;
    > a {
      color: white;
      font-size: 1.1rem;
    }
  }
`;

const List = styled.li``;

const Toggle = styled.div`
  padding: 8px;
  font-size: 1.2rem;
`;

const Icon = styled.i`
  cursor: pointer;
`;

const Main = styled.div`
  max-width: 896px;
  margin: 0px auto;
  padding: 32px 16px;
`;

const Container = styled.div``;

const Section = styled.div``;

const Tablist = styled.div``;

const TabUlist = styled.ul`
  display: flex;
  width: 100%;
  padding: 0px 32px;
`;

const IssueTab = styled.li`
    font-size: 1.2rem;
    width: 100%;
    text-align: center;
    border-radius: 6px 6px 0px 0px;
    border-top: 1px solid rgb(225, 228, 232);
    border-right: 1px solid rgb(225, 228, 232);
    border-left: 1px solid rgb(225, 228, 232);

    span {
      cursor: pointer;
      color: rgb(88, 96, 105);
      padding: 16px;
      display: block;
      width: 100%;
    }
}
`;

const PullreqTab = styled.li`
  font-size: 1.2rem;
  width: 100%;
  text-align: center;
  border-radius: 6px 6px 0px 0px;
  border-bottom: 1px solid rgb(225, 228, 232);

  span {
    cursor: pointer;
    color: rgb(88, 96, 105);
    padding: 16px;
    display: block;
    width: 100%;
  }
`;

const IssueTags = styled.span`
  cursor: pointer;
  color: rgb(88, 96, 105);
  padding: 16px;
  display: block;
  width: 100%;
`;

const PullreqTags = styled.span`
  cursor: pointer;
  color: rgb(88, 96, 105);
  padding: 16px;
  display: block;
  width: 100%;
`;

function Home() {
  return <h2>トップページ</h2>;
}

function Issue() {
  return <h2>issue</h2>;
}

function Pullrequest() {
  return <h2>Pull-request</h2>;
}

function App() {
  return (
    <div className="App">
      <Header>
        <Link to="/">
          <Gihtubviewer>Github Viewer</Gihtubviewer>
        </Link>
        <Ulist>
          <List>
            <Link to="/issue">Issue</Link>
          </List>
          <List>
            <Link to="/pull-request">Pull Request</Link>
          </List>
        </Ulist>
        <Toggle>
          <Icon className="fa fa-bars"></Icon>
        </Toggle>
      </Header>
      <Main>
        <Container>
          <Section>
          <Routes>
                <Route path="/" element={<Home />} />
                <Route path="issue" element={<Issue />} />
                <Route path="pull-request" element={<Pullrequest />} />
              </Routes>
            <Tablist>
              <TabUlist>
                <IssueTab>
                  <IssueTags>Issue</IssueTags>
                </IssueTab>
                <PullreqTab>
                  <PullreqTags>Pull Request</PullreqTags>
                </PullreqTab>
              </TabUlist>
            </Tablist>
          </Section>
        </Container>
      </Main>
    </div>
  );
}

export default App;
