import { createStore } from 'redux';

function getdate(){
  const date = new Date();
  const YYYY = date.getFullYear();
  var MM = date.getMonth()+1;
  var DD = date.getDate();

  if(MM <10){
    MM = "0" + MM;
  }
  if(DD < 10){
    DD = "0" + DD;
  }

  return MM + "-" + DD + "-" + YYYY;
}

const initialData = {
  user:{
      name: 'jiro',
      mail: 'hoge@example.com',
  },
  issue:{
     1:{
      id: 1,
      title: 'A bug in Top Page',
      status: 'Open',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
      ctuser: '',
      ctdate: getdate(),
      update: getdate(),
    },
    2:{
      id: 2,
      title: 'A problem of performance in Top Page',
      status: 'Open',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
      ctuser: '',
      ctdate: getdate(),
      update: getdate(),
    },
    3:{
      id: 3,
      title: 'fix layout',
      status: 'Open',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
      ctuser: '',
      ctdate: getdate(),
      update: getdate(),
    },
  }
};

const initialState = {
  index: Object.values(initialData.issue).length,
  data: initialData
}

const reducer = (state = initialState, action) => {
  let newState = {...state}
  switch (action.type) {
    case 'add_issue':
      const { title, description } = action.payload || {}
      newState.index++
      newState.data.issue[newState.index] = { id: newState.index, title: title, status: 'Open', ctuser: initialData.user.name, ctdate: getdate(), update: getdate()};
      return newState;
    case 'filter_issue':
      const greptxt = action.payload

      newState.data.issue = Object.values(initialData.issue).filter( function( value ) {
        return value.title.includes(greptxt)
      })
      return newState;
    case 'edit_issue':
        console.log('edit')
        const { id,titleedit, descriptionedit } = action.payload || {}
        newState.data.issue[id].title = titleedit
        newState.data.issue[id].description = descriptionedit
        return newState
    default:
    return state;
  }
};

const store = createStore(reducer);
export default store;
