import styled from 'styled-components';
import { connect } from 'react-redux';
import React from 'react';
import Modal from 'react-modal';

const Section = styled.div`
  padding: 16px;
`;

const Container = styled.div`
  padding: 16px;
  margin-top: 16px;
`;

const Action = styled.div``;

const Inheader = styled.div`
  display: flex;
  -webkit-box-align: center;
  align-items: center;
`;

const Issueheader = styled.h2``;

const Searchblock = styled.div`
  padding: 8px 16px;
  display: flex;
  width: 100%;
  .text-field-container {
    width: 100%;
  }
`;

const Searchtext = styled.div`
  border-radius: 6px;
  border: 1px solid rgb(225, 228, 232);
`;

const Searchinput = styled.input`
  padding: 8px;
  border: none;
  outline: none;
  background: none;
  font-size: 1rem;
  width: 100%;
`;

const ActionButtons = styled.div`
  display: flex;
`;

const Newbutton = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(66, 195, 96);
  border-bottom: 2px solid rgb(40, 167, 69);
`;

const Deletebutton = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(215, 58, 73);
  border-bottom: 2px solid rgb(175, 28, 42);
`;

const Lists = styled.div`
  overflow: scroll;
`;

const Issuetable = styled.table`
  border: 1px solid rgb(225, 228, 232);
  border-radius: 6px;

  th:first-child,
  td:first-child {
    min-width: auto;
  }

  th,
  td {
    padding: 8px;
    text-align: left;
    min-width: 10rem;
    border-bottom: 1px solid rgb(225, 228, 232);
  }
`;

const Issuetr = styled.tr`
  cursor: pointer;
`;

const Modallabel = styled.div`
  max-width: 598px;
  margin: auto;
`;

const Modallabeltext = styled.h2`
  padding: 0px 8px;
`;

const Modalcontents = styled.div`
  padding: 32px 0px 16px;
`;

const Modalcontentstitle = styled.div`
  padding: 16px;
`;

const Modalcontentstitlelabel = styled.label`
  display: block;
  padding: 8px 0px;
`;

const Modalcontentstitleinput = styled.div`
  border-radius: 6px;
  border: 1px solid rgb(225, 228, 232);
`;

const Modalcontentstitleinputtext = styled.input`
  padding: 8px;
  border: none;
  outline: none;
  background: none;
  font-size: 1rem;
  width: 100%;
`;

const Modalcontentsdescription = styled.div`
  padding: 16px;
`;

const Modalcontentsdescriptiontitle = styled.label`
  display: block;
  padding: 8px 0px;

`;
const customStyles = {
  content: {
    position: 'absolute',
    inset: '40px',
    border: '1px solid rgb(204, 204, 204)',
    background: 'rgb(255, 255, 255)',
    overflow: 'auto',
    borderradius: '4px',
    outline: 'none',
    padding: '20px',
  },
};

function Issue({ issue }) {
  const [modalIsOpen, setIsOpen] = React.useState(false);
  function openModal() {
    setIsOpen(true);
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <Section>
      <Container>
        <Action>
          <Inheader>
            <Issueheader>Issue</Issueheader>
            <Searchblock>
              <Searchtext className="text-field-container">
                <Searchinput type="input" placeholder="issue名で検索" />
              </Searchtext>
            </Searchblock>
            <ActionButtons>
              <Newbutton onClick={() => openModal()}>New</Newbutton>
              <Deletebutton>Delete</Deletebutton>
            </ActionButtons>
          </Inheader>
        </Action>
        <Lists>
          <Issuetable>
            <thead>
              <tr>
                <th>
                  <input type="checkbox" />
                </th>
                <th></th>
                <th>ステータス</th>
                <th>作成者</th>
                <th>作成日付</th>
                <th>更新日付</th>
              </tr>
            </thead>
            <Modal
              isOpen={modalIsOpen}
              onRequestClose={closeModal}
              style={customStyles}
              contentLabel="Example Modal"
            >
              <Modallabel>
                <Modallabeltext>Issueを追加</Modallabeltext>
                <Modalcontents>
                  <Modalcontentstitle>
                    <Modalcontentstitlelabel>タイトル</Modalcontentstitlelabel>
                    <Modalcontentstitleinput>
                      <Modalcontentstitleinputtext placeholder="タイトルを入力してください"></Modalcontentstitleinputtext>
                    </Modalcontentstitleinput>
                  </Modalcontentstitle>
                  <Modalcontentsdescription>

                  </Modalcontentsdescription>
                </Modalcontents>
              </Modallabel>
            </Modal>
            <tbody>
              {issue.map((key) => (
                <Issuetr>
                  <td>
                    <input type="checkbox" />
                  </td>
                  <td>{key.title}</td>
                  <td>{key.status}</td>
                  <td>{key.ctuser}</td>
                  <td>{key.ctdate}</td>
                  <td>{key.update}</td>
                </Issuetr>
              ))}
            </tbody>
          </Issuetable>
        </Lists>
      </Container>
    </Section>
  );
}

const mapStateToProps = (state) => {
  return { issue: state.issue };
};

export default connect(mapStateToProps)(Issue);
