import styled from 'styled-components';
import { connect } from 'react-redux';

const Section = styled.div`
  padding: 16px;
`;

const Container = styled.div`
  padding: 16px;
  margin-top: 16px;
`;

const Action = styled.div``;

const Inheader = styled.div`
  display: flex;
  -webkit-box-align: center;
  align-items: center;
`;

const Issueheader = styled.h2``;

const Searchblock = styled.div`
  padding: 8px 16px;
  display: flex;
  width: 100%;
  .text-field-container {
    width: 100%;
  }
`;

const Searchtext = styled.div`
  border-radius: 6px;
  border: 1px solid rgb(225, 228, 232);
`;

const Searchinput = styled.input`
  padding: 8px;
  border: none;
  outline: none;
  background: none;
  font-size: 1rem;
  width: 100%;
`;

const ActionButtons = styled.div`
  display: flex;
`;

const Newbutton = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(66, 195, 96);
  border-bottom: 2px solid rgb(40, 167, 69);
`;

const Deletebutton = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(215, 58, 73);
  border-bottom: 2px solid rgb(175, 28, 42);
`;

const Lists = styled.div`
  overflow: scroll;
`;

const Issuetable = styled.table`
  border: 1px solid rgb(225, 228, 232);
  border-radius: 6px;

  th:first-child,
  td:first-child {
    min-width: auto;
  }

  th,
  td {
    padding: 8px;
    text-align: left;
    min-width: 10rem;
    border-bottom: 1px solid rgb(225, 228, 232);
  }
`;

const Issuetr = styled.tr`
  cursor: pointer;
`;

function Issue({ title }) {
  return (
    <Section>
      <Container>
        <Action>
          <Inheader>
            <Issueheader>Issue</Issueheader>
            <Searchblock>
              <Searchtext className="text-field-container">
                <Searchinput type="input" placeholder="issue名で検索" />
              </Searchtext>
            </Searchblock>
            <ActionButtons>
              <Newbutton>New</Newbutton>
              <Deletebutton>Delete</Deletebutton>
            </ActionButtons>
          </Inheader>
        </Action>
        <Lists>
          <Issuetable>
            <thead>
              <tr>
                <th>
                  <input type="checkbox" />
                </th>
                <th></th>
                <th>ステータス</th>
                <th>作成者</th>
                <th>作成日付</th>
                <th>更新日付</th>
              </tr>
            </thead>
            <tbody>
              <Issuetr>
                <td>{title}</td>
              </Issuetr>
            </tbody>
          </Issuetable>
        </Lists>
      </Container>
    </Section>
  );
}

const mapStateToProps = (state) => {
  return { title: state.title };
};

export default connect(mapStateToProps)(Issue);
