import { createStore } from 'redux';

const initialState = {
  issue: [
    {
      id: 1,
      title: 'A bug in Top Page',
      status: 'Open',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
      ctuser: '',
      ctdate: '05-05-2022',
      update: '05-05-2022',
    },
    {
      id: 2,
      title: 'A problem of performance in Top Page',
      status: 'Open',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
      ctuser: '',
      ctdate: '05-05-2022',
      update: '05-05-2022',
    },
    {
      id: 3,
      title: 'fix layout',
      status: 'Open',
      description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit.',
      ctuser: '',
      ctdate: '05-05-2022',
      update: '05-05-2022',
    },
  ],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'add_issue':
    console.log('ok');
  }
  return state;
};

const store = createStore(reducer);
export default store;
