const Modalcontentsdescriptionyextarea = styled.textarea`
    padding: 8px;
    border: none;
    outline: none;
    background: none;
    font-size: 1rem;
    width: 100%;
`;

const descriptionblock = (props) => {

    return(
        <Modalcontentsdescriptionyextarea
        placeholder="説明を入力してください"
        defaultValue={props.default}
        ></Modalcontentsdescriptionyextarea>
    )
}

export default descriptionblock
