import styled from 'styled-components';
import React from 'react';

const Alertarea = styled.div`
padding: 16px;
min-height: 100px;
`;

const Alerttext = styled.p`
color: rgb(215, 58, 73);
background: rgba(215, 58, 73, 0.35);
padding: 8px;
border-radius: 6px;
`;

const Modal_alert = (props) => {

return (
    <Alertarea>
    {props.error &&
        <Alerttext>{props.error.message}を入力してください</Alerttext>
    }
    </Alertarea>
    )
}

export default Modal_alert
