import styled from 'styled-components';
import React, { useState } from 'react';

const Container = styled.div``;

const Section = styled.div``;

const Tablist = styled.div``;

const TabUlist = styled.ul`
  display: flex;
  width: 100%;
  padding: 0px 32px;
`;

const IssueTab = styled.li`
    font-size: 1.2rem;
    width: 100%;
    text-align: center;
    border-radius: 6px 6px 0px 0px;
    border-top: 1px solid rgb(225, 228, 232);
    border-right: 1px solid rgb(225, 228, 232);
    border-left: 1px solid rgb(225, 228, 232);

    span {
      cursor: pointer;
      color: rgb(88, 96, 105);
      padding: 16px;
      display: block;
      width: 100%;
    }
}
`;

const PullreqTab = styled.li`
  font-size: 1.2rem;
  width: 100%;
  text-align: center;
  border-radius: 6px 6px 0px 0px;
  border-bottom: 1px solid rgb(225, 228, 232);

  span {
    cursor: pointer;
    color: rgb(88, 96, 105);
    padding: 16px;
    display: block;
    width: 100%;
  }
`;

const IssueTags = styled.span`
  cursor: pointer;
  color: rgb(88, 96, 105);
  padding: 16px;
  display: block;
  width: 100%;
`;

const PullreqTags = styled.span`
  cursor: pointer;
  color: rgb(88, 96, 105);
  padding: 16px;
  display: block;
  width: 100%;
`;

function Home() {
  const [state, setState] = useState(true);

  const updateState = () => {
    setState(!state);
  };

  return (
    <Container>
      <Section>
        <Tablist>
          <TabUlist>
            <IssueTab>
              <IssueTags onclick={updateState}>{state ? 'true' : 'false'}</IssueTags>
            </IssueTab>
            <PullreqTab>
              <PullreqTags>Pull Request</PullreqTags>
            </PullreqTab>
          </TabUlist>
        </Tablist>
      </Section>
    </Container>
  );
}

export default Home;
