import { Link } from 'react-router-dom';
import styled from 'styled-components';
import React, { useState } from 'react';

const Header = styled.div`
  display: flex;
  padding: 16px;
  -webkit-box-pack: justify;
  justify-content: space-between;
  -webkit-box-align: center;
  align-items: center;
  width: 100%;
  background: rgb(51, 51, 51);
  color: white;
`;

const Gihtubviewer = styled.h1`
  white-space: nowrap;
  color: white;
`;

const Ulist = styled.ul`
  display: flex;
  width: 100%;
  padding: 0px 32px;
`;

const List = styled.li`
  list-style-type: none;
  margin-right: 16px;
`;

const Toggle = styled.div`
  padding: 8px;
  font-size: 1.2rem;
`;

const Icon = styled.i`
  cursor: pointer;
`;

const Main = styled.div`
  max-width: 896px;
  margin: 0px auto;
  padding: 32px 16px;
`;

const Container = styled.div``;

const Section = styled.div``;

const Tablist = styled.div``;

const TabUlist = styled.ul`
  display: flex;
  > li {
    padding: 0px;
  }
`;

const IssueTab = styled.li`
    font-size: 1.2rem;
    width: 100%;
    text-align: center;
    border-radius: 6px 6px 0px 0px;
    border-top: 1px solid rgb(225, 228, 232);
    border-right: 1px solid rgb(225, 228, 232);
    border-left: 1px solid rgb(225, 228, 232);
}
`;

const PullreqTab = styled.li`
  font-size: 1.2rem;
  width: 100%;
  text-align: center;
  border-radius: 6px 6px 0px 0px;
  border-bottom: 1px solid rgb(225, 228, 232);
}
`;

const IssueTags = styled.span`
  cursor: pointer;
  color: rgb(88, 96, 105);
  padding: 16px;
  display: block;
  width: 100%;
  background-color: ${(props) => (props.active ? 'red' : 'blue')};
`;

const PullreqTags = styled.span`
  cursor: pointer;
  color: rgb(88, 96, 105);
  padding: 16px;
  display: block;
  width: 100%;
  background-color: ${(props) => (props.active ? 'blue' : 'red')};
`;

function App() {
  const [isClick, setIsClick] = useState(false);

  const handleClick = () => {
    setIsClick(!isClick);
  };

  return (
    <div className="App">
      <Header>
        <Link to="/about">
          <Gihtubviewer>Github Viewer</Gihtubviewer>
        </Link>
        <Ulist>
          <List>Issue</List>
          <List>Pull Request</List>
        </Ulist>
        <Toggle>
          <Icon className="fa fa-bars"></Icon>
        </Toggle>
      </Header>
      <Main>
        <Container>
          <Section>
            <Tablist>
              <TabUlist>
                <IssueTab>
                  <IssueTags onClick={handleClick} active={isClick}>
                    Issue
                  </IssueTags>
                </IssueTab>
                <PullreqTab>
                  <PullreqTags onClick={handleClick} active={isClick}>
                    Pull Request
                  </PullreqTags>
                </PullreqTab>
              </TabUlist>
            </Tablist>
          </Section>
        </Container>
      </Main>
    </div>
  );
}

export default App;
