import { Routes, Route, Link } from 'react-router-dom';
import styled from 'styled-components';
import React, { useState } from 'react';
import Home from './components/templates/home';
import Issue from './components/templates/issue';
import Profile from './components/templates/profile';
import Pullrequest from './components/templates/pullrequest';

const Header = styled.div`
  display: flex;
  padding: 16px;
  -webkit-box-pack: justify;
  justify-content: space-between;
  -webkit-box-align: center;
  align-items: center;
  width: 100%;
  background: rgb(51, 51, 51);
  color: white;
`;

const Gihtubviewer = styled.h1`
  white-space: nowrap;
  color: white;
`;

const Ulist = styled.ul`
  display: flex;
  width: 100%;
  padding: 0px 32px;

  li {
    margin-right: 16px;
    > a {
      color: white;
      font-size: 1.1rem;
    }
  }
`;

const List = styled.li``;

const Main = styled.div`
  max-width: 896px;
  margin: 0px auto;
  padding: 32px 16px;
`;

const Toggletab = styled.li`
  a {
    display: block;
    color: rgb(51, 51, 51);
    padding: 8px;
    font-size: 1rem;
  }
`;

const Togglelink = styled.div`
  position: absolute;
  margin-top: 16px;
  right: 16px;
  width: 200px;
  border-radius: 2px;
  padding: 8px 0px;
  background: white;
  color: rgb(51, 51, 51);
  box-shadow: rgb(51 51 51 / 15%) 1px 1px 4px 1px;
`;

const Toggle = styled.div`
  padding: 8px;
  font-size: 1.2rem;
`;

const Icon = styled.i`
  cursor: pointer;
`;

function App() {
  const [isShown, setIsShown] = useState(false);

  function setshow() {
    setIsShown(!isShown);
    console.log('ok');
  }
  return (
    <div className="App">
      <Header>
        <Link to="/">
          <Gihtubviewer>Github Viewer</Gihtubviewer>
        </Link>
        <Ulist>
          <List>
            <Link to="/issue">Issue</Link>
          </List>
          <List>
            <Link to="/pull-request">Pull Request</Link>
          </List>
        </Ulist>
        <Toggle>
          <Icon className="fa fa-bars" onclick={setIsShown(!isShown)}></Icon>
          <Togglelink>
            <ul>
              <Toggletab>
                <Link to="/">Top</Link>
              </Toggletab>
              <Toggletab>
                <Link to="/profile">Your Profile</Link>
              </Toggletab>
              <Toggletab>
                <Link to="/issue">Issue</Link>
              </Toggletab>
              <Toggletab>
                <Link to="/pull-request">Pull Request</Link>
              </Toggletab>
            </ul>
          </Togglelink>
        </Toggle>
      </Header>
      <Main>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="profile" element={<Profile />} />
          <Route path="issue" element={<Issue />} />
          <Route path="pull-request" element={<Pullrequest />} />
        </Routes>
      </Main>
    </div>
  );
}

export default App;
