import styled from 'styled-components';
import createStore from 'redux';

const Section = styled.div`
  padding: 16px;
`;

const Container = styled.div`
  padding: 16px;
  margin-top: 16px;
`;

const Action = styled.div``;

const Inheader = styled.div`
  display: flex;
  -webkit-box-align: center;
  align-items: center;
`;

const Issueheader = styled.h2``;

const Searchblock = styled.div`
  padding: 8px 16px;
  display: flex;
  width: 100%;
  .text-field-container {
    width: 100%;
  }
`;

const Searchtext = styled.div`
  border-radius: 6px;
  border: 1px solid rgb(225, 228, 232);
`;

const Searchinput = styled.input`
  padding: 8px;
  border: none;
  outline: none;
  background: none;
  font-size: 1rem;
  width: 100%;
`;

const ActionButtons = styled.div`
  display: flex;
`;

const Newbutton = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(66, 195, 96);
  border-bottom: 2px solid rgb(40, 167, 69);
`;

const Deletebutton = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(215, 58, 73);
  border-bottom: 2px solid rgb(175, 28, 42);
`;

const Lists = styled.div`
  overflow: scroll;
`;

const Issuetable = styled.table`
  border: 1px solid rgb(225, 228, 232);
  border-radius: 6px;

  th:first-child,
  td:first-child {
    min-width: auto;
  }

  th,
  td {
    padding: 8px;
    text-align: left;
    min-width: 10rem;
    border-bottom: 1px solid rgb(225, 228, 232);
  }
`;

function addTodo(todo) {
  return { type: 'ADD_TODO', payload: todo };
}

function removeTodo (todo) {
  return { type: 'REMOVE_TODO', payload: todo };
}

function Issue() {
  const ADD_TODO = 'ADD_TODO';
  const REMOVE_TODO = 'REMOVE_TODO';
  const initialData = {
    1: { id: 1, text: 'brush a tooth' },
    2: { id: 2, text: 'wash a face' },
    3: { id: 3, text: 'drink a coffee' },
  };

  const initialState = {
    index: Object.values(initialData).length,
    data: initialData,
  };

  const reducer = (state = initialState, action) => {
    let newState = {...state};
    const { id, text } = action.payload || {}
    switch (action.type) {
      case ADD_TODO:
        newState.index++;
        newState.data[newState.index] = { id: newState.index, text };
        return newState;
      case REMOVE_TODO:
        let newData = {...newState.data}
        delete newData[id];
        return {
          ...newState,
          data: newData,
        };
      default:
        return state;
    }
  };

  var store = Redux.createStore(reducer);

  return (
    <Section>
      <Container>
        <Action>
          <Inheader>
            <Issueheader>Issue</Issueheader>
            <Searchblock>
              <Searchtext className="text-field-container">
                <Searchinput type="input" placeholder="issue名で検索" />
              </Searchtext>
            </Searchblock>
            <ActionButtons>
              <Newbutton>New</Newbutton>
              <Deletebutton>Delete</Deletebutton>
            </ActionButtons>
          </Inheader>
        </Action>
        <Lists>
          <Issuetable>
            <thead>
              <tr>
                <th>
                  <input type="checkbox" />
                </th>
                <th></th>
                <th>ステータス</th>
                <th>作成者</th>
                <th>作成日付</th>
                <th>更新日付</th>
              </tr>
            </thead>
            <tbody></tbody>
          </Issuetable>
        </Lists>
      </Container>
    </Section>
  );
}

export default Issue;
