import styled, { createGlobalStyle } from 'styled-components';
import { connect } from 'react-redux';
import React from 'react';
import Modal from 'react-modal';

const Section = styled.div`
  padding: 16px;
`;

const Container = styled.div`
  padding: 16px;
  margin-top: 16px;
`;

const Action = styled.div``;

const Inheader = styled.div`
  display: flex;
  -webkit-box-align: center;
  align-items: center;
`;

const Issueheader = styled.h2``;

const Searchblock = styled.div`
  padding: 8px 16px;
  display: flex;
  width: 100%;
  .text-field-container {
    width: 100%;
  }
`;

const Searchtext = styled.div`
  border-radius: 6px;
  border: 1px solid rgb(225, 228, 232);
`;

const Searchinput = styled.input`
  padding: 8px;
  border: none;
  outline: none;
  background: none;
  font-size: 1rem;
  width: 100%;
`;

const ActionButtons = styled.div`
  display: flex;
`;

const Newbutton = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(66, 195, 96);
  border-bottom: 2px solid rgb(40, 167, 69);
`;

const Deletebutton = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(215, 58, 73);
  border-bottom: 2px solid rgb(175, 28, 42);
`;

const Lists = styled.div`
  overflow: scroll;
`;

const Issuetable = styled.table`
  border: 1px solid rgb(225, 228, 232);
  border-radius: 6px;

  th:first-child,
  td:first-child {
    min-width: auto;
  }

  th,
  td {
    padding: 8px;
    text-align: left;
    min-width: 10rem;
    border-bottom: 1px solid rgb(225, 228, 232);
  }
`;

const Issuetr = styled.tr`
  cursor: pointer;
`;

const Modallabel = styled.div`
  max-width: 598px;
  margin: auto;
  textarea {
    min-height: 150px;
  }
  a {
    width: auto;
  }
`;

const Modallabeltext = styled.h2`
  padding: 0px 8px;
`;

const Modalcontents = styled.div`
  padding: 32px 0px 16px;
`;

const Modalcontentstitle = styled.div`
  padding: 16px;
`;

const Modalcontentstitlelabel = styled.label`
  display: block;
  padding: 8px 0px;
`;

const Modalcontentstitleinput = styled.div`
  border-radius: 6px;
  border: 1px solid rgb(225, 228, 232);
`;

const Modalcontentstitleinputtext = styled.input`
  padding: 8px;
  border: none;
  outline: none;
  background: none;
  font-size: 1rem;
  width: 100%;
`;

const Modalcontentsdescription = styled.div`
  padding: 16px;
`;

const Modalcontentsdescriptiontitle = styled.label`
  display: block;
  padding: 8px 0px;
`;

const Modalcontentsdescriptiontext = styled.div`
  border-radius: 6px;
  border: 1px solid rgb(225, 228, 232);
`;

const Modalcontentsdescriptionyextarea = styled.textarea`
  padding: 8px;
  border: none;
  outline: none;
  background: none;
  font-size: 1rem;
  width: 100%;
`;

const Modalspace = styled.div`
  padding: 16px;
  min-height: 100px;
`;

const Modalbuttons = styled.div`
  display: flex;
  -webkit-box-align: center;
  align-items: center;
  -webkit-box-pack: end;
  justify-content: flex-end;
  padding: 8px;
`;

const Modalbuttoncreate = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  color: white;
  font-size: 1rem;
  background: rgb(66, 195, 96);
  border-bottom: 2px solid rgb(40, 167, 69);
`;

const Modalbuttonexit = styled.a`
  cursor: pointer;
  display: block;
  width: 100%;
  text-align: center;
  padding: 4px 16px;
  margin: 4px;
  min-width: 100px;
  border-radius: 6px;
  font-size: 1rem;
`;
const GlobalStyle = createGlobalStyle`
