import styled, { createGlobalStyle } from 'styled-components';
import { connect } from 'react-redux';
import React from 'react';
import Modal from 'react-modal';
import Buttons from '../atoms/button';
import Statusblock from '../molecules/Statusblock';
import Inheaders from '../organisms/inheader';
import Issuetr from '../organisms/Issuetr';
import Modallabeltext from '../atoms/titlelabel';
import Titleblock from '../molecules/Titleblock';
import Descriptionblock from '../molecules/descriptionblock';
import Alert from '../molecules/Alertblock';


const Checkbox = styled.input`
`;

const Section = styled.div`
  padding: 16px;
`;

const Container = styled.div`
  padding: 16px;
  margin-top: 16px;
`;

const Action = styled.div``;

const Lists = styled.div`
  overflow: scroll;
`;

const Issuetable = styled.table`
  border: 1px solid rgb(225, 228, 232);
  border-radius: 6px;

  th:first-child,
  td:first-child {
    min-width: auto;
  }

  th,
  td {
    padding: 8px;
    text-align: left;
    min-width: 10rem;
    border-bottom: 1px solid rgb(225, 228, 232);
  }
`;



const Modallabel = styled.div`
  max-width: 598px;
  margin: auto;
  textarea {
    min-height: 150px;
  }
  a {
    width: auto;
  }
`;

const Modalcontents = styled.div`
  padding: 32px 0px 16px;
`;

const Modalbuttons = styled.div`
  display: flex;
  -webkit-box-align: center;
  align-items: center;
  -webkit-box-pack: end;
  justify-content: flex-end;
  padding: 8px;
`;

Checkbox.defaultProps = { 
  type: 'checkbox'
};

const GlobalStyle = createGlobalStyle`
  @media (max-width: 576px) {
    .ReactModal__Content {
      width: 100%;
      right: 0px !important;
      left: 0px !important;
    }
  }
  .ReactModal__Content {
    margin: auto;
    width: 60%;
  }
`;

const customStyles = {
  content: {
    position: 'absolute',
    inset: '40px',
    border: '1px solid rgb(204, 204, 204)',
    background: 'rgb(255, 255, 255)',
    overflow: 'auto',
    borderradius: '4px',
    outline: 'none',
    padding: '20px',
  },
};

function Issue({ issue,add_issue,edit_issue,delete_issue }) {
  const [modalIsOpen, setIsOpen] = React.useState(false);
  const [modalIsOpenEdit, setIsOpenEdit] = React.useState(false);
  const [text, setText] = React.useState('');
  const [description, setDescription] = React.useState('');
  const [vals, setVal] = React.useState('');
  const [textedit, setTextEdit] = React.useState('');
  const [descriptionedit, setDescriptionEdit] = React.useState('');
  const [statusedit, setStatusEdit] = React.useState('');
  const [error, setError] = React.useState('');
  const [check, setcheck] = React.useState([]);

  const List = Object.values(issue);
  const Status = ['Open','Close']

  const onSubmit = () => {
    if (!text) {
      setError({message:'タイトル'})
      return
    }
    if (!description) {
      setError({message:'説明'})
      return
    }
    add_issue({title:text,description:description})
    setError('');
    setText('');
    setDescription('');
    setIsOpen(false)
  };

  const onSubmitEdit = () => {
    if(!textedit){
      setError({message:'タイトル'})
      return;
    }
    if(!descriptionedit){
      setError({message:'説明'})
      return;
    }
    edit_issue({id:vals.id,textedit:textedit,descriptionedit:descriptionedit,statusedit:statusedit})
    setError('')
    setIsOpenEdit(false)
  }

  const openEdit = (val) => {
    setIsOpenEdit(true)
    setVal(val)
    setTextEdit(val.title)
    setDescriptionEdit(val.description)
    setStatusEdit(val.status)
  }

  const closeModal = () => {
    setText('')
    setDescription('')
    setError('')
    setIsOpen(false)
  }

  const closeModalEdit = () => {
    setError('')
    setIsOpenEdit(false)
  }

  const checkedbox = (e) => {
    e.stopPropagation()
    const { id, checked } = e.target
    setcheck([...check, id])
    if (!checked) {
      setcheck(check.filter(item => item !== id))
    }
  }

  const AllChecked = () => {
    if(check.length === List.length){
      setcheck([])
    }else{
      const tmp = List.map(function(val){
        return val.id.toString()
      });
      setcheck(tmp)
    }
  }

  return (
    <Section>
      <Container>
        <Action>
            <Inheaders open={() => setIsOpen(true)} delete={() => delete_issue(check)} />
        </Action>
        <Lists>
          <Issuetable>
            <thead>
              <tr>
                <th>
                  <input type="checkbox" onChange={AllChecked}/>
                </th>
                <th></th>
                <th>ステータス</th>
                <th>作成者</th>
                <th>作成日付</th>
                <th>更新日付</th>
              </tr>
            </thead>
            <React.Fragment>
              <GlobalStyle />
            </React.Fragment>

            <Modal
              isOpen={modalIsOpen}
              style={customStyles}
              contentLabel="Example Modal"
            >
              <Modallabel>
                <Modallabeltext>Issueを追加</Modallabeltext>
                <Modalcontents>
                  <Titleblock 
                  onChange={e => setText(e.target.value)}
                  placeholder="タイトルを入力してください"
                  />
                  <Descriptionblock 
                  onChange={e => setDescription(e.target.value)}
                  placeholder="説明を入力してください"
                  />
                </Modalcontents>
                <Alert error={error} />
                <Modalbuttons>
                  <Buttons color="white" background="rgb(66, 195, 96)" borderbottom="2px solid rgb(40, 167, 69)" onClick={() => onSubmit()}>
                    作成
                  </Buttons>
                  <Buttons onClick={closeModal}>
                    閉じる
                  </Buttons>
                </Modalbuttons>
              </Modallabel>
            </Modal>

            <Modal
              isOpen={modalIsOpenEdit}
              style={customStyles}
              contentLabel="Example Modal"
            >
              <Modallabel>
                <Modallabeltext>Issueを追加</Modallabeltext>
                <Modalcontents>
                  <Titleblock 
                  default={vals.title} 
                  onChange={e => setTextEdit(e.target.value)}
                  placeholder="タイトルを入力してください"
                  />
                  <Descriptionblock 
                  default={vals.description} 
                  onChange={e => setDescriptionEdit(e.target.value)}
                  placeholder="説明を入力してください"
                  />
                <Statusblock 
                onChange={e => setStatusEdit(e.target.value)}
                />
                </Modalcontents>
                <Alert error={error} />
                <Modalbuttons>
                  <Buttons 
                  color="white" 
                  background="rgb(66, 195, 96)" 
                  borderbottom="2px solid rgb(40, 167, 69)" 
                  onClick={() => onSubmitEdit()}
                  >
                    更新
                  </Buttons>
                  <Buttons onClick={() => closeModalEdit()}>
                    閉じる
                  </Buttons>
                </Modalbuttons>
              </Modallabel>
            </Modal>

            <tbody>
              {List.map((val,key) =>
                <Issuetr 
                key={key}
                openEdit={() => openEdit(val)}
                />
              )}
            </tbody>
          </Issuetable>
        </Lists>
      </Container>
    </Section>
  );
}

const mapStateToProps = (state) => {
  return { issue: state.data };
};

const mapDispatchToProps = (dispatch) => {
  return {
    add_issue: (list) => dispatch({ type: 'add_issue', payload: list }),
    edit_issue: (edittxt) => dispatch({type: 'edit_issue', payload: edittxt}),
    delete_issue: (list) => dispatch({ type: 'delete_issue', payload: list }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Issue);
